function pt=getpt(A,t)% bezier/getpt - Berekent bezierpuntif length(t)==1	pt=A.pt_lijst;	for i=2:length(pt)		for j=1:length(pt)-i+1			pt(j,:)=(1.0-t)*pt(j,:) + t*pt(j+1,:);		end	end	pt=pt(1,:);else	pt=zeros(length(t),2);	for k=1:length(t)		p=A.pt_lijst;		for i=2:length(p)			for j=1:length(p)-i+1				p(j,:)=(1.0-t(k))*p(j,:) + t(k)*p(j+1,:);			end		end		pt(k,:)=p(1,:);	endend