function H=gethufftables(c,nr)% CJPEG/GETHUFFTABLES - Geeft de huffman tables in JPEG-object%     H=gethufftables(c[,nr])i=strmatch('DHT',{c.data.markerName});if ~exist('nr','var')	nr=[];endif ~isempty(nr)	if nr>length(i)		error('Er zijn niet voldoende huffman tables')	else		i=i(nr);	endelseif length(i)>1	warning('!Meerdere DHT-definities!')	i=i(1);elseif isempty(i)	error('Geen Huffman-definities')endH=maakjpghuftable(double(c.data(i).data));