function Q=getquanttables(c,nr)% CJPEG/GEQUANTTABLES - Geeft de huffman tables in JPEG-object%    Q=getquanttables(c)i=strmatch('DQT',{c.data.markerName});if ~exist('nr','var')	nr=[];endif ~isempty(nr)	if nr>length(i)		error('Er zijn niet voldoende quantizationtables')	else		i=i(nr);	endelseif length(i)>1	warning('!Meerdere DQT-definities!')	i=i(1);elseif isempty(i)	error('Geen quantisatie definities')endQ=reshape(c.data(i).data,65,length(c.data(i).data)/65)';