function jc=calcjc(d,m,j,h,mn,s)% CALCJC - Berekent Julian Centuries%   ref Astronomical Algorithms - J. Meeuws - chap. 25%  jc=calcjc(d,m,j[,h,mn,s])%  jc=calcjc([d,m,j,h,mn,s)%  jc=calcjc(JD)if nargin==1	if size(d,2)>1		jd=calcjd(d);	else		if all(d>100000&d<900000)	% matlab time			jd=d+1721058.5-1/24;		elseif all(d<3)	% extended from 1 to 3 for long timeseries(!!)			jd=d*36525+2451545;		elseif all(d>1e6&d<4e6)			jd=d;		else			error('Unknown input')		end	endelse	if nargin>3		d=d+h/24;		if nargin>4			d=d+mn/24/60;			if nargin>5				d=d+s/24/3600;			end		end	end	jd=calcjd(d,m,j);endjc=(jd-2451545)/36525;