function Y=fdct2f(X)% FDCT2F - twee-dimensionele DCT-tranformatie met even lengtesn = length(X);i1=[1:2:n n:-2:2];% Compute weights to multiply DFT coefficientsww = (exp(-i*(0:n-1)*pi/(2*n))/sqrt(n/2)).';ww(1) = ww(1) / sqrt(2);ww = ww(:,ones(1,n));  % Double the weights for even-length case and make squareY = real(ww.*fft(X(i1,:)));Y = real(ww.'.*fft(Y(:, i1),[],2));