function lk=lkurtosis(X)%LKURTOSIS - Bepalen van L-kurtosis%     lk=lkurtosis(X)N=length(X);Y=sort(X);Y=Y(:);mY2=[2/(N*(N-1))*(N-1:-1:1)*Y(1:N-1) 2/(N*(N-1))*(1:N-1)*Y(2:N)];mY4=zeros(1,4);n4=0;for i=1:N-3    f41 = (N - i) * (N - i - 1) * (N - i - 2) / 6;    f42 = i * (N - 1 - i) * (N - 2 - i) / 2;    f43 = i * (i + 1) * (N - i - 2) / 2;    f44 = (i + 2) * (i + 1) * i / 6;	mY4(1)=mY4(1)+f41 * Y(i);	mY4(2)=mY4(2)+f42 * Y(i+1);	mY4(3)=mY4(3)+f43 * Y(i+2);	mY4(4)=mY4(4)+f44 * Y(i+3);    n4 = n4 + f41;endmY4=mY4/n4;lk=(mY4(4)-3*mY4(3)+3*mY4(2)-mY4(1))/diff(mY2)/2;