function H=maakjpghuftable(d)% MAAKJPGHUFTABLE - Maak huffman tabeli=1;H=struct('type',[],'N',[],'x',[],'codes',[],'sizes',[],'tabel',[],	...	'valPtr',[],'minCodes',[],'maxCodes',[]);nH=0;while i<length(d)	nH=nH+1;	% is nH>1 possible? (is loop needed?)	if nH>1		warning('Toch! - nH>1')	end	typ=d(i);	n=d(i+1:i+16);	i1=i+17+sum(n);	x=d(i+17:i1-1);	[codes,sizes]=maakcode(n);	tabel=zeros(256,2);	tabel(x+1,1)=codes';	tabel(x+1,2)=sizes';	maxCodes=cumsum(n);	valPtr=[1 maxCodes(1:15)+1];	minCodes=codes(min(end,valPtr));	j=find(n);	maxCodes(j)=codes(maxCodes(j));	maxCodes(n==0)=-1;	H(nH)=struct('type',typ,'N',n,'x',x,	...		'codes',codes,'sizes',sizes,	...		'tabel',tabel,	...		'valPtr',valPtr,'minCodes',minCodes,'maxCodes',maxCodes	...		);	i=i1;endfunction [codes,sizes]=maakcode(n)sn=cumsum(n);ln=sn(end);code=0;i=0;s=1;codes=zeros(1,ln);sizes=zeros(1,ln);while i<ln	while i>=sn(s)		code=code*2;		s=s+1;	end	i=i+1;	codes(i)=code;	code=code+1;	sizes(i)=s;end