       -2; 		   2,	         2;		  -2,	        -2; 		   14,	       13;		  -63,	       13;		   136,	      -236; 		   273,	       1065;		   251,        149;		  -25,	      -9; 		   9,	        -2;		  -8,          7;		   2,	        -10;		   19,	       35;		   10,	       3;	];	PLUTOdata.mARG =ARG;	PLUTOdata.mLONG=LONG/1e6*pi/180;	PLUTOdata.mLAT =LAT/1e6*pi/180;	PLUTOdata.mRAD =RAD/1e7;endswitch lower(bCalcMethod)case 'chapront'	% vermits de ene in JD werkt, en de andere in JC, wordt deze datum-bepaling (nog) apart uitgevoerd.	if length(T)>1		T=calcjd(T);	elseif T<100	% julian century		T=calcjd(T);	% omzetting naar julian day	end	Fx=73060;	T=T-2341972.5;	x=T-Fx;	T=T/Fx-1;	% omzetting naar bereik -1..1 voor 1/1/1700 tot 24/1/2100	t=cumprod([1;T;T;T]);	f=PLUTOdata.per*x;	cF=cos(f);	sF=sin(f);	f=PLUTOdata.poisson*x;	cF1=cos(f);	sF1=sin(f);	f=PLUTOdata.pois2*x;	cF2=cos(f);	sF2=sin(f);	X=PLUTOdata.secX*t	...		+cF*PLUTOdata.CXSXper(:,1)+sF*PLUTOdata.CXSXper(:,2)	...		+T*((cF1*PLUTOdata.SXCXpois(:,2)+sF1*PLUTOdata.SXCXpois(:,1))	...			+T*(cF2*PLUTOdata.CXSXpois2(:,1)+sF2*PLUTOdata.CXSXpois2(:,2)));	Y=PLUTOdata.secY*t	...		+cF*PLUTOdata.CYSYper(:,1)+sF*PLUTOdata.CYSYper(:,2)	...		+T*((cF1*PLUTOdata.SYCYpois(:,2)+sF1*PLUTOdata.SYCYpois(:,1))	...			+T*(cF2*PLUTOdata.CYSYpois2(:,1)+sF2*PLUTOdata.CYSYpois2(:,2)));	Z=PLUTOdata.secZ*t	...		+cF*PLUTOdata.CZSZper(:,1)+sF*PLUTOdata.CZSZper(:,2)	...		+T*((cF1*PLUTOdata.SZCZpois(:,2)+sF1*PLUTOdata.SZCZpois(:,1))	...			+T*(cF2*PLUTOdata.CZSZpois2(:,1)+sF2*PLUTOdata.CZSZpois2(:,2)));		if nargout==2		% bereken snelheden		dt=[1;2*T;3*T*T];		% ?misschien toch beter tussenresultaten van vorige gebruiken?		dX=PLUTOdata.secX(2:4)*dt/Fx	...			+sum((cF'.*PLUTOdata.CXSXper(:,2)-sF'.*PLUTOdata.CXSXper(:,1)).*PLUTOdata.per')	...			+T*sum((cF1'.*PLUTOdata.SXCXpois(:,1)-sF1'.*PLUTOdata.SXCXpois(:,2)).*PLUTOdata.poisson')	...			+(cF1*PLUTOdata.SXCXpois(:,2)+sF1*PLUTOdata.SXCXpois(:,1))/Fx	...			+T*T*sum((cF2'.*PLUTOdata.CXSXpois2(:,2)-sF2'.*PLUTOdata.CXSXpois2(:,1)).*PLUTOdata.pois2')	...			+2*T*(cF2*PLUTOdata.CXSXpois2(:,1)+sF2*PLUTOdata.CXSXpois2(:,2))/Fx;		dY=PLUTOdata.secY(2:4)*dt/Fx	...			+sum((cF'.*PLUTOdata.CYSYper(:,2)-sF'.*PLUTOdata.CYSYper(:,1)).*PLUTOdata.per')	...			+T*sum((cF1'.*PLUTOdata.SYCYpois(:,1)-sF1'.*PLUTOdata.SYCYpois(:,2)).*PLUTOdata.poisson')	...			+(cF1*PLUTOdata.SYCYpois(:,2)+sF1*PLUTOdata.SYCYpois(:,1))/Fx	...			+T*T*sum((cF2'.*PLUTOdata.CYSYpois2(:,2)-sF2'.*PLUTOdata.CYSYpois2(:,1)).*PLUTOdata.pois2')	...			+2*T*(cF2*PLUTOdata.CYSYpois2(:,1)+sF2*PLUTOdata.CYSYpois2(:,2))/Fx;		dZ=PLUTOdata.secZ(2:4)*dt/Fx	...			+sum((cF'.*PLUTOdata.CZSZper(:,2)-sF'.*PLUTOdata.CZSZper(:,1)).*PLUTOdata.per')	...			+T*sum((cF1'.*PLUTOdata.SZCZpois(:,1)-sF1'.*PLUTOdata.SZCZpois(:,2)).*PLUTOdata.poisson')	...			+(cF1*PLUTOdata.SZCZpois(:,2)+sF1*PLUTOdata.SZCZpois(:,1))/Fx	...			+T*T*sum((cF2'.*PLUTOdata.CZSZpois2(:,2)-sF2'.*PLUTOdata.CZSZpois2(:,1)).*PLUTOdata.pois2')	...			+2*T*(cF2*PLUTOdata.CZSZpois2(:,1)+sF2*PLUTOdata.CZSZpois2(:,2))/Fx;	end		if ~exist('bRect','var')||bRect		if nargout>2			long=X;			lat=Y;			D=Z;		else			long=[X Y Z];			if nargout==2				lat=[dX dY dZ];			end		end		return	else		l=atan2(Y,X);		D=sqrt(X*X+Y*Y+Z*Z);		lat=asin(Z/D);	endcase 'meeus'	if length(T)>1		T=calcjc(T);	elseif T>100	% julian century		T=calcjc(T);	% omzetting naar julian day	end	J=34.35+3034.9057*T;	S=50.08+1222.1138*T;	P=238.96+144.9600*T;	angleDAT=[J;S;P]*pi/180;	alpha=PLUTOdata.mARG*angleDAT;	l=(238.958116 + 144.96*T)*pi/180+sum(sum(PLUTOdata.mLONG.*[sin(alpha) cos(alpha)]));	lat=-3.908239*pi/180+sum(sum(PLUTOdata.mLAT.*[sin(alpha) cos(alpha)]));	D=40.7241346+sum(sum(PLUTOdata.mRAD.*[sin(alpha) cos(alpha)]));	if ~exist('bRect','var')||bRect		sineps=0.397777156;		coseps=0.917482062;		X=D*cos(l)*cos(lat);		Y=D*(sin(l)*cos(lat)*coseps-sin(lat)*sineps);		Z=D*(sin(l)*cos(lat)*sineps+sin(lat)*coseps);		if nargout>