function [th0out,th]=calctsidereal(varargin)% CALCTSIDEREAL - Calculate siderealtime%   ref Astronomical Algorithms - J. Meeuws - chap. 12if nargin==1&length(varargin{1})==1	if varargin{1}>10000		T=calcjc(varargin{1});	else		T=varargin{1};	endelse	T=calcjc(varargin{:});endjd=T*36525;th0=(280.46061837+360.98564736629*jd+(0.000387933-T/38710000).*T.*T)/180*pi;th0=th0-2*pi*floor(th0/2/pi);if nargout==1	th0out=th0;else	[eps,deps,eps0,dpsi]=calcobl(T,0);	th=th0+dpsi*cos(eps);	if nargout		th0out=th0;	else		hr0=th0/pi*12;		mn0=(hr0-floor(hr0))*60;		hr0=floor(hr0);		s0=(mn0-floor(mn0))*60;		mn0=floor(mn0);		hr=th/pi*12;		mn=(hr-floor(hr))*60;		hr=floor(hr);		s=(mn-floor(mn))*60;		mn=floor(mn);		fprintf('%2d:%02d:%07.4f     -     %2d:%02d:%07.4f\n',hr0,mn0,s0,hr,mn,s)	endend