function rad=deg2rad(d,mn,sec)%DEG2RAD  - Graden naar radialen%       rad = deg2rad(d,mn[,sec])%       rad = deg2rad([d,mn,sec]) (sec is optional)%   (!!!) currently standard Matlab function only converting degrees (no min,sec)!!!!if length(d)>1	if nargin>1		error('combinatie van meerdere inputs en meerdere waarden voor "d" is incorrect')	end	mn=d(2);	if length(d)>2		sec=d(3);	else		sec=0;	end	d=d(1);elseif nargin<3	sec=0;	if nargin<2		mn=0;	endendrad=(d+(mn+sec/60)/60)*pi/180;