function [alpha,delout]=ecli2equcoor(lambda,beta,epsil)% equeclicoor - Zet eclitische coordinaten om in equatoriale%   ref Astronomical Algorithms - J. Meeuws - chap. 13%  aldel=ecli2equcoor(lambda,beta,epsil)% of%  ...=ecli2equcoor([lambda,beta],epsil)% of%  [alpha,delta]=....if nargin==2	epsil=beta;	beta=lambda(2);	lambda=lambda(1);endalpha=atan2(sin(lambda).*cos(epsil)-tan(beta).*sin(epsil),cos(lambda));if isscalar(alpha)	if alpha<0		alpha=alpha+2*pi;	endelse	alpha=alpha+2*pi*(alpha<0);enddelta=asin(sin(beta).*cos(epsil)+cos(beta).*sin(epsil).*sin(lambda));if nargout==1	alpha=[alpha,delta];else	delout=delta;end