function D = leesvsopcompleet(direct)% LEESVSOPCOMPLEET - Leest complete VSOP-data (alle planeten)%    D = leesvsopcompleet([direct])if exist('VSOP87data.mat','file')	fprintf('Data wordt gehaald uit Matlab-VSOP87-data.\n');	load VSOP87data D	returnendif ~exist('direct','var')	direct = fullfile(fileparts(which('leesvsopcompleet')),'vsop87data');endd=dir(fullfile(direct,'*.DAT'));if isempty(d)	error('Kan data niet vinden!')endfprintf('Data wordt gehaald uit VSOP87-tekst-data.\n');D=cell(1,length(d));for i=1:length(D)	D{i}=leesvsopdata(fullfile(direct,d(i).name));end