function h=magnnoord(plaats)%MAGNNOORD - Magnetisch noorden in graden%  h=magnnoord(plaats)if ~exist('plaats','var')	plaats='';endd1=now;lplaats=lower(plaats);if ~isempty(findstr(lplaats,'leuven'))|~isempty(findstr(lplaats,'kessel-lo'))	h=3+35/60;	% tov Y lambert	dh=7/60;	d0=datenum([1985 6 30]);else	warning('Plaats niet gekend')	h=3+35/60;	dh=0;	d0=now;endh=h+(d1-d0)/365.25*dh;