function luit=plotzwdata(S)%PLOTZWDATA - Plot zonnewijzer-data%!!! nog geen rekening gehouden met opties bij data!!ccc=[0 0 0];teken=S.data.teken;l=zeros(1,length(teken)+2);for i=1:length(teken)	switch teken(i).type	case 'L'		L=GetLijn(S,teken(i).data);		X=L.coor;		l(i)=line(X(:,1),X(:,2),X(:,3),'color',ccc);	endendi=length(teken)+3;l(i-2)=line(S.spiegel(1)+[0 0],S.spiegel(2)+[0 0],[0 S.spiegel(3)],'color',ccc	...	,'Marker','o','MarkerSize',15,'Tag','spiegel'	...	);l(i-1)=line(S.spiegel(1),S.spiegel(2),S.spiegel(3),'color',ccc	...	,'Marker','*','MarkerSize',15,'Tag','spiegel'	...	);axis equalif ~isempty(S.data.camerapositie)	set(gca,'CameraPosition',S.data.camerapositie)endif ~isempty(S.data.cameratarget)	set(gca,'CameraTarget',S.data.cameratarget)endif ~isempty(S.data.cameraviewangle)	set(gca,'CameraViewAngle',S.data.cameraviewangle)endset(gca,'UserData',struct('Huis',l))if nargout	luit=l;endfunction L=GetLijn(S,nr)i=find(cat(2,S.data.L.nr)==nr);if isempty(i)	error('Onbekend lijnnummer')endL=S.data.L(i);