function [A,h]=tolocalhorizcoor(varargin)% tolocalhorizcoor - Zet om naar locale horizontale coordinaten%   ref Astronomical Algorithms - J. Meeuws - chap. 13%%      Ah=tolocalhorizcoor(H,phi,delta)%   of%      Ah=tolocalhorizcoor(T,obs_long,obs_lat,rasc,decl)% kijk ook naar calcposhemel%    meer of minder hier?% !!!bovendien meer samen nemen!!!!if nargin==1	H=varargin{1}(1);	phi=varargin{1}(2);	delta=varargin{1}(3);	A=atan2(sin(H),cos(H)*sin(phi)-tan(delta)*cos(phi));	h=asin(sin(phi).*sin(delta)+cos(phi).*cos(delta).*cos(H));elseif nargin==3	H=varargin{1};	phi=varargin{2};	delta=varargin{3};	A=atan2(sin(H),cos(H)*sin(phi)-tan(delta)*cos(phi));	h=asin(sin(phi).*sin(delta)+cos(phi).*cos(delta).*cos(H));elseif nargin==5	T=varargin{1};	obs_long=varargin{2};	% observer longitude	obs_lat=varargin{3};			% observer lattitude	if isempty(obs_long)&isempty(obs_lat)		obs_long=5/180*pi;	% ergens in Belgie			% !!!!geografische longitude		obs_lat=50.75/180*pi;	end	rasc=varargin{4};	decl=varargin{5};	[th0,th]=calctsidereal(T);	H=th+obs_long-rasc;	% (ander teken dan bij Meeus vanwege omgekeerde geo-longitude	A=atan2(sin(H),cos(H).*sin(obs_lat)-tan(decl).*cos(obs_lat));	h=asin(sin(obs_lat).*sin(decl)+cos(obs_lat).*cos(decl).*cos(H));else	error('Verkeerd gebruik van tolocalhorizcoor')endif nargout==1	Ah=[A h];end